library(hamcrest)
cummax.foo <- function(x) 41
Math.bar <- function(x) 44
test.cummax.1 <- function() assertThat(cummax(c(TRUE, TRUE, FALSE, FALSE, TRUE)), identicalTo(c(1L, 1L, 1L, 1L, 1L), tol = 0.000100))
test.cummax.2 <- function() assertThat(cummax(1:3), identicalTo(1:3, tol = 0.000100))
test.cummax.3 <- function() assertThat(cummax(c(1L, NA, 4L, NA, 999L)), identicalTo(c(1L, NA, NA, NA, NA), tol = 0.000100))
test.cummax.4 <- function() assertThat(cummax(c(3.14159, 6.28319, 9.42478, 12.5664, 15.708)), identicalTo(c(3.14159, 6.28319, 9.42478, 12.5664, 15.708), tol = 0.000100))
test.cummax.5 <- function() assertThat(cummax(c(-3.14159, -6.28319, -9.42478, -12.5664, -15.708)), identicalTo(c(-3.14159, -3.14159, -3.14159, -3.14159, -3.14159), tol = 0.000100))
test.cummax.6 <- function() assertThat(cummax(c(1L, 2L, 1073741824L, 1073741824L)), identicalTo(c(1L, 2L, 1073741824L, 1073741824L), tol = 0.000100))
test.cummax.7 <- function() assertThat(cummax(structure(1:2, .Names = c("a", "b"))), identicalTo(structure(1:2, .Names = c("a", "b")), tol = 0.000100))
test.cummax.8 <- function() assertThat(cummax(structure(c(1.5, 2.5), .Names = c("a", "b"))), identicalTo(structure(c(1.5, 2.5), .Names = c("a", "b")), tol = 0.000100))
test.cummax.9 <- function() assertThat(cummax(structure(1:12, .Dim = 3:4)), identicalTo(1:12, tol = 0.000100))
test.cummax.10 <- function() assertThat(cummax(c("4.1", "blahh", "99.9", "-413", NA)), identicalTo(c(4.1, NA, NA, NA, NA), tol = 0.000100))
test.cummax.11 <- function() assertThat(cummax(structure(1:3, rando.attrib = 941L)), identicalTo(1:3, tol = 0.000100))
test.cummax.12 <- function() assertThat(cummax(structure(1:3, .Dim = 3L, .Dimnames = list(c("a", "b", "c")))), identicalTo(structure(1:3, .Names = c("a", "b", "c")), tol = 0.000100))
test.cummax.13 <- function() assertThat(cummax(numeric(0)), identicalTo(numeric(0), tol = 0.000100))
test.cummax.14 <- function() assertThat(cummax(integer(0)), identicalTo(integer(0), tol = 0.000100))
test.cummax.15 <- function() assertThat(cummax(structure(integer(0), .Names = character(0))), identicalTo(structure(integer(0), .Names = character(0)), tol = 0.000100))
test.cummax.16 <- function() assertThat(cummax(structure("foo", class = "foo")), identicalTo(41))
test.cummax.17 <- function() assertThat(cummax(structure(list(1L, "bar"), class = "bar")), identicalTo(44))
