library(hamcrest)
cummin.foo <- function(x) 41
Math.bar <- function(x) 44
test.cummin.1 <- function() assertThat(cummin(c(TRUE, TRUE, FALSE, FALSE, TRUE)), identicalTo(c(1L, 1L, 0L, 0L, 0L), tol = 0.000100))
test.cummin.2 <- function() assertThat(cummin(1:3), identicalTo(c(1L, 1L, 1L), tol = 0.000100))
test.cummin.3 <- function() assertThat(cummin(c(1L, NA, 4L, NA, 999L)), identicalTo(c(1L, NA, NA, NA, NA), tol = 0.000100))
test.cummin.4 <- function() assertThat(cummin(c(3.14159, 6.28319, 9.42478, 12.5664, 15.708)), identicalTo(c(3.14159, 3.14159, 3.14159, 3.14159, 3.14159), tol = 0.000100))
test.cummin.5 <- function() assertThat(cummin(c(-3.14159, -6.28319, -9.42478, -12.5664, -15.708)), identicalTo(c(-3.14159, -6.28319, -9.42478, -12.5664, -15.708), tol = 0.000100))
test.cummin.6 <- function() assertThat(cummin(c(1L, 2L, 1073741824L, 1073741824L)), identicalTo(c(1L, 1L, 1L, 1L), tol = 0.000100))
test.cummin.7 <- function() assertThat(cummin(structure(1:2, .Names = c("a", "b"))), identicalTo(structure(c(1L, 1L), .Names = c("a", "b")), tol = 0.000100))
test.cummin.8 <- function() assertThat(cummin(structure(c(1.5, 2.5), .Names = c("a", "b"))), identicalTo(structure(c(1.5, 1.5), .Names = c("a", "b")), tol = 0.000100))
test.cummin.9 <- function() assertThat(cummin(structure(1:12, .Dim = 3:4)), identicalTo(c(1L, 1L, 1L, 1L, 1L, 1L, 1L, 1L, 1L, 1L, 1L, 1L), tol = 0.000100))
test.cummin.10 <- function() assertThat(cummin(c("4.1", "blahh", "99.9", "-413", NA)), identicalTo(c(4.1, NA, NA, NA, NA), tol = 0.000100))
test.cummin.11 <- function() assertThat(cummin(structure(1:3, rando.attrib = 941L)), identicalTo(c(1L, 1L, 1L), tol = 0.000100))
test.cummin.12 <- function() assertThat(cummin(structure(1:3, .Dim = 3L, .Dimnames = list(c("a", "b", "c")))), identicalTo(structure(c(1L, 1L, 1L), .Names = c("a", "b", "c")), tol = 0.000100))
test.cummin.13 <- function() assertThat(cummin(numeric(0)), identicalTo(numeric(0), tol = 0.000100))
test.cummin.14 <- function() assertThat(cummin(integer(0)), identicalTo(integer(0), tol = 0.000100))
test.cummin.15 <- function() assertThat(cummin(structure(integer(0), .Names = character(0))), identicalTo(structure(integer(0), .Names = character(0)), tol = 0.000100))
test.cummin.16 <- function() assertThat(cummin(structure("foo", class = "foo")), identicalTo(41))
test.cummin.17 <- function() assertThat(cummin(structure(list(1L, "bar"), class = "bar")), identicalTo(44))
