library(hamcrest)
cumprod.foo <- function(x) 41
Math.bar <- function(x) 44
test.cumprod.1 <- function() assertThat(cumprod(c(TRUE, TRUE, FALSE, FALSE, TRUE)), identicalTo(c(1, 1, 0, 0, 0), tol = 0.000100))
test.cumprod.2 <- function() assertThat(cumprod(1:3), identicalTo(c(1, 2, 6), tol = 0.000100))
test.cumprod.3 <- function() assertThat(cumprod(c(1L, NA, 4L, NA, 999L)), identicalTo(c(1, NA, NA, NA, NA), tol = 0.000100))
test.cumprod.4 <- function() assertThat(cumprod(c(3.14159, 6.28319, 9.42478, 12.5664, 15.708)), identicalTo(c(3.14159, 19.7392068721, 186.037682144031, 2337.82392889475, 36722.5382750787), tol = 0.000100))
test.cumprod.5 <- function() assertThat(cumprod(c(-3.14159, -6.28319, -9.42478, -12.5664, -15.708)), identicalTo(c(-3.14159, 19.7392068721, -186.037682144031, 2337.82392889475, -36722.5382750787), tol = 0.000100))
test.cumprod.6 <- function() assertThat(cumprod(c(1L, 2L, 1073741824L, 1073741824L)), identicalTo(c(1, 2, 2147483648, 2305843009213693952), tol = 0.000100))
test.cumprod.7 <- function() assertThat(cumprod(structure(1:2, .Names = c("a", "b"))), identicalTo(structure(c(1, 2), .Names = c("a", "b")), tol = 0.000100))
test.cumprod.8 <- function() assertThat(cumprod(structure(c(1.5, 2.5), .Names = c("a", "b"))), identicalTo(structure(c(1.5, 3.75), .Names = c("a", "b")), tol = 0.000100))
test.cumprod.9 <- function() assertThat(cumprod(structure(1:12, .Dim = 3:4)), identicalTo(c(1, 2, 6, 24, 120, 720, 5040, 40320, 362880, 3628800, 39916800, 479001600), tol = 0.000100))
test.cumprod.10 <- function() assertThat(cumprod(c("4.1", "blahh", "99.9", "-413", NA)), identicalTo(c(4.1, NA, NA, NA, NA), tol = 0.000100))
test.cumprod.11 <- function() assertThat(cumprod(structure(1:3, rando.attrib = 941L)), identicalTo(c(1, 2, 6), tol = 0.000100))
test.cumprod.12 <- function() assertThat(cumprod(structure(1:3, .Dim = 3L, .Dimnames = list(c("a", "b", "c")))), identicalTo(structure(c(1, 2, 6), .Names = c("a", "b", "c")), tol = 0.000100))
test.cumprod.13 <- function() assertThat(cumprod(numeric(0)), identicalTo(numeric(0), tol = 0.000100))
test.cumprod.14 <- function() assertThat(cumprod(integer(0)), identicalTo(numeric(0), tol = 0.000100))
test.cumprod.15 <- function() assertThat(cumprod(structure(integer(0), .Names = character(0))), identicalTo(structure(numeric(0), .Names = character(0)), tol = 0.000100))
test.cumprod.16 <- function() assertThat(cumprod(structure("foo", class = "foo")), identicalTo(41))
test.cumprod.17 <- function() assertThat(cumprod(structure(list(1L, "bar"), class = "bar")), identicalTo(44))
